def load_multi_year_csv(base_url, years):
    dfs = []
    for i in years:
        url = f"{base_url}{i}.csv"
        try:
            df = pd.read_csv(url)
            df["Year"] = i
            dfs.append(df)
        except Exception as e:
            print(f"載入 {i}.csv 失敗：{e}")
    return pd.concat(dfs, ignore_index = True)

