def extract_original_html_from_github_raw(github_raw_html_content):
    soup = BeautifulSoup(github_raw_html_content, 'html.parser')
    line_contents = soup.select('.line-wrap .line-content')

    if not line_contents:
        return github_raw_html_content

    reconstructed_html_lines = []
    for line_content_td in line_contents:
        reconstructed_html_lines.append(line_content_td.get_text(separator="", strip=False))

    reconstructed_html = "\n".join(reconstructed_html_lines)
    return reconstructed_html

def parse_url_to_df(url, stat_type='batting'):
    try:
        response = requests.get(url)
        response.raise_for_status()
        response.encoding = 'utf-8'
        github_wrapped_html = response.text
    except Exception as e:
        print(f"下載失敗: {e}")
        return None

    html_content = extract_original_html_from_github_raw(github_wrapped_html)

    if not html_content.strip():
        print(f"未能從 {url} 中提取到有效的 HTML 內容")
        return None

    soup = BeautifulSoup(html_content, 'html.parser')
    rows = soup.select('.RecordTable tr')
    data = []

    for row in rows:
        team_node = row.select_one('.team_logo a')
        name_node = row.select_one('.name a')

        if not team_node: continue

        player_name = name_node.text.strip() if name_node else "Unknown"
        raw_team_name = team_node.get('title') or team_node.text
        team_name = raw_team_name.replace('統一7-ELEVEn獅', '統一獅')

        cols = row.select('td.num')
        if not cols: continue

        try:
            def clean_int(text_val):
                return int(text_val.replace(',', '').replace('（', '').replace('）', ''))

            if stat_type == 'batting':
                stats = {
                    'Team': team_name,
                    'Name': player_name,
                    'Team Name': team_name,
                    'Team ID': 'UNK',
                    'PA': clean_int(cols[2].text),
                    'AB': clean_int(cols[3].text),
                    'H': clean_int(cols[6].text),
                    'TB': clean_int(cols[11].text),
                    'BB': clean_int(cols[13].text),
                    'HBP': clean_int(cols[15].text),
                    'SO': clean_int(cols[16].text),
                    'SF': clean_int(cols[19].text)
                }
            elif stat_type == 'pitching':
                ip_raw = cols[2].text.replace(',', '')
                if '.' in ip_raw:
                    inn, part = ip_raw.split('.')
                    ip = int(inn) + (int(part) / 3.0)
                else:
                    ip = float(ip_raw)

                stats = {
                    'Team': team_name,
                    'Name': player_name,
                    'Team Name': team_name,
                    'Team ID': 'UNK',
                    'IP': ip,
                    'H': clean_int(cols[7].text),
                    'BB': clean_int(cols[10].text),
                    'ER': clean_int(cols[14].text)
                }
            data.append(stats)
        except (ValueError, IndexError) as e:
            continue

    return pd.DataFrame(data)

