def align_and_concat(df_historical, df_new, common_cols):
    all_cols = list(set(df_historical.columns) | set(df_new.columns))

    df_historical_aligned = df_historical.reindex(columns=all_cols)
    df_new_aligned = df_new.reindex(columns=all_cols)

    if 'ID' not in df_new_aligned.columns:
        df_new_aligned['ID'] = -1
    if 'Name' not in df_new_aligned.columns:
        df_new_aligned['Name'] = 'Unknown'
    if 'Team ID' not in df_new_aligned.columns and 'Team' in df_new_aligned.columns:
        df_new_aligned['Team ID'] = df_new_aligned['Team'].map({
            "統一獅": "ADD", "中信兄弟": "ACC", "樂天桃猿": "AHG",
            "味全龍": "AAA", "富邦悍將": "AEO", "台鋼雄鷹": "ACN"
        }).fillna('UNK')
    if 'Team Name' not in df_new_aligned.columns:
        df_new_aligned['Team Name'] = df_new_aligned['Team']

    df_new_aligned['Year'] = df_new_aligned['Year'].astype(int)
    df_historical_aligned['Year'] = df_historical_aligned['Year'].astype(int)

    combined_df = pd.concat([df_historical_aligned, df_new_aligned], ignore_index=True)
    return combined_df

