def add_prefix(df, prefix, keep_cols=("ID","Name","Team ID","Team Name","Year")):
    rename_map = {}
    for c in df.columns:
        if c not in keep_cols:
            rename_map[c] = f"{prefix}{c}"
    return df.rename(columns=rename_map)

