"""## 賽事勝負預測模型

### 需要的公式
* **FIP** = ((13 * HR) + (3 * (BB+HBP)) - (2 * SO)) / IP + Constant
* **wRC+** = (((wRAA per PA + league runs per PA) + (league runs per PA - ballpark factor x league runs per PA) / league wRC per plate appearance, not including pitchers)) x 100.
* **wOBA** = ((0.72 x NIBB) + (0.75 x HBP) + (0.90 x 1B) + (0.92 x RBOE) + (1.24 x 2B) + (1.56 x 3B) + (1.95 x HR) + (.25 x SB) - (.50 x CS)) / PA
    
    *（但我用簡化版的，沒用RBOE、RISP）*
* **wRAA** = wRAA = ((wOBA - wOBA of the entire league) / annual wOBA scale) x PA
* **IBB** = bat_BB - bat_IBB
* **Park Factor**
    ![球場校正值.jpg](data:image/jpeg;base64,)
    來源：https://www.threads.com/@dasusun/post/DO8qWUwCcYN
"""

# 球場因子
PARK_FACTORS = {
    "洲際": {"Runs": 1.19, "HR": 1.55},
    "澄清湖": {"Runs": 1.03, "HR": 1.13},
    "天母": {"Runs": 0.96, "HR": 1.07},
    "新莊": {"Runs": 0.90, "HR": 1.02},
    "樂天桃園": {"Runs": 1.18, "HR": 0.92},
    "台南": {"Runs": 0.91, "HR": 0.88},
    "台北大巨蛋": {"Runs": 0.84, "HR": 0.60}
}

# 球場分配函數
def assign_stadium_v2(row):
    team = row['Home']
    year = row['Year']
    if team == "味全龍" and year >= 2024:
        return "台北大巨蛋"
    mapping = {
        "統一7-ELEVEn獅": "台南", "中信兄弟": "洲際", "樂天桃猿": "樂天桃園",
        "富邦悍將": "新莊", "台鋼雄鷹": "澄清湖", "味全龍": "天母"
    }
    return mapping.get(team, "天母")

# 隨機抓取對手投手的函數
def get_random_sp(row):
    opponents = pitchers[(pitchers['Team'] == row['Away']) & (pitchers['Year'] == row['Year'])]['Name_clean']
    return opponents.iloc[0] if not opponents.empty else row['Home_SP']

def train_meta_learner_pro(df_games, df_bat, df_pit):
    # 指標計算
    df_pit['FIP'] = ((13 * df_pit['pit_HR']) + (3 * (df_pit['pit_BB'] + df_pit['pit_HBP'])) - (2 * df_pit['pit_SO'])) / df_pit['pit_IP'] + 3.1
    df_bat['NIBB'] = df_bat['bat_BB'] - df_bat['bat_IBB']
    df_bat['wOBA'] = ((0.72 * df_bat['NIBB']) + (0.75 * df_bat['bat_HBP']) + (0.90 * df_bat['bat_1B']) +
                      (1.24 * df_bat['bat_2B']) + (1.56 * df_bat['bat_3B']) + (1.95 * df_bat['bat_HR']) +
                      (0.25 * df_bat['bat_SB']) - (0.50 * df_bat['bat_CS'])) / df_bat['bat_PA']

    league_woba = df_bat['wOBA'].mean()
    df_bat['wRAA'] = ((df_bat['wOBA'] - league_woba) / 1.2) * df_bat['bat_PA']

    # 建立特徵矩陣
    X, y = [], []
    for _, game in df_games.iterrows():
        h_fip = df_pit[(df_pit['Name_clean'] == game['Home_SP']) & (df_pit['Year'] == game['Year'])]['FIP'].mean()
        a_fip = df_pit[(df_pit['Name_clean'] == game['Away_SP']) & (df_pit['Year'] == game['Year'])]['FIP'].mean()

        h_wraa = df_bat[(df_bat['Team Name_x'] == game['Home']) & (df_bat['Year'] == game['Year'])]['wRAA'].sum()
        a_wraa = df_bat[(df_bat['Team Name_x'] == game['Away']) & (df_bat['Year'] == game['Year'])]['wRAA'].sum()

        pf_val = PARK_FACTORS.get(game['Stadium'], {"Runs": 1.0})["Runs"]

        if pd.notnull([h_wraa, a_wraa, h_fip, a_fip]).all():
            X.append([h_wraa - a_wraa, a_fip - h_fip, pf_val])
            y.append(1 if game['Home_Score'] > game['Away_Score'] else 0)

    # 標準化
    scaler = StandardScaler()
    X_scaled = scaler.fit_transform(X)

    model = LogisticRegression(class_weight='balanced')
    model.fit(X_scaled, y)

    joblib.dump(model, 'cpbl_meta_learner.pkl')
    joblib.dump(scaler, 'scaler.pkl')
    return model, scaler

source_df = master.copy()
df_games = source_df[['Year', 'Team Name_x', 'Team Name_y', 'bat_R', 'pit_R', 'Name_clean']].copy()
df_games.columns = ['Year', 'Home', 'Away', 'Home_Score', 'Away_Score', 'Home_SP']

df_games['Away_SP'] = df_games.apply(get_random_sp, axis=1)
df_games['Stadium'] = df_games.apply(assign_stadium_v2, axis=1)

meta_model, scaler = train_meta_learner_pro(df_games, batters, pitchers)

print("=== Meta-learner 結過 ===")
weights = meta_model.coef_[0]
features = ['wRAA 差距 (打擊)', 'FIP 差距 (投球)', '球場校正值 (PF)']
weight_df = pd.DataFrame({'特徵': features, '影響權重 (Beta)': weights})
print(weight_df)
print(f"\n基礎勝率偏移 (Intercept): {meta_model.intercept_[0]:.4f}")

